CREATE VIEW `loan_audit_view`  AS SELECT `loans`.`loan_number` AS `loan_number`, sum(`loan_schedules`.`amount_paid`) AS `amount_paid`, `member_accounts`.`balance` AS `account_balance` FROM ((`loan_schedules` join `loans` on((`loans`.`id` = `loan_schedules`.`loan_id`))) join `member_accounts` on((`member_accounts`.`id` = `loans`.`member_account_id`))) WHERE isnull(`loans`.`deleted_at`) GROUP BY `loans`.`loan_number` ;
